import '../../../styles/main.css';
import {
    MDBContainer,
    MDBBtnGroup,
    MDBAccordion,
    MDBAccordionItem,
    MDBRadio,
} from 'mdb-react-ui-kit';
import { IVideos, getVideos } from '../../../scripts/requests';
import { useEffect, useState } from 'react';

interface IVideosProps {
    movieId: number;
}

const Videos = (props: IVideosProps) => {
    const [videos, setVideos] = useState<IVideos | null>(null);

    useEffect(() => {
        fetchVideos('pt-BR');
    });

    const fetchVideos = async (language: string) => {
        if (props.movieId > 0) {
            setVideos(await getVideos(props.movieId, language));
        }
    };

    const videosLanguageClick = (language: string) => {
        fetchVideos(language);
    };

    return (
        <>
            <MDBContainer className="mt-3">
                <h2 className="text-info">Videos</h2>
                <MDBBtnGroup className="mb-3">
                    <MDBRadio
                        id="ptVideosButton"
                        name="videosLanguage"
                        className=""
                        wrapperClass="me-2 transparentBG"
                        onClick={() => videosLanguageClick('pt-BR')}
                        btn
                        wrapperTag="span"
                        label="Português"
                        defaultChecked
                    />
                    <MDBRadio
                        id="enVideosButton"
                        name="videosLanguage"
                        onClick={() => videosLanguageClick('en-US')}
                        wrapperTag="span"
                        btn
                        label="Inglês"
                    />
                </MDBBtnGroup>
                {videos && (
                    <>
                        {videos.trailers && videos.trailers.length > 0 && (
                            <>
                                <h6 className="text-info">Trailers</h6>
                                <MDBAccordion
                                    flush
                                    style={{
                                        minWidth: '100%',
                                    }}
                                >
                                    {videos.trailers.map((video, index) => (
                                        <MDBAccordionItem
                                            key={index}
                                            collapseId={index + 1}
                                            headerTitle={video.name}
                                            style={{
                                                backgroundColor:
                                                    'rgba(0, 0, 0, 0.5)',
                                            }}
                                        >
                                            <div className="video-container">
                                                <iframe
                                                    title={video.name}
                                                    src={`https://www.youtube.com/embed/${video.key}`}
                                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                ></iframe>
                                            </div>
                                        </MDBAccordionItem>
                                    ))}
                                </MDBAccordion>
                            </>
                        )}
                        {videos.teasers && videos.teasers.length > 0 && (
                            <>
                                <h6 className="text-info mt-2">Teasers</h6>
                                <MDBAccordion
                                    flush
                                    style={{
                                        minWidth: '100%',
                                    }}
                                >
                                    {videos.teasers.map((video, index) => (
                                        <MDBAccordionItem
                                            key={index}
                                            collapseId={index + 1}
                                            headerTitle={video.name}
                                            style={{
                                                backgroundColor:
                                                    'rgba(0, 0, 0, 0.5)',
                                            }}
                                        >
                                            <div className="video-container">
                                                <iframe
                                                    title={video.name}
                                                    src={`https://www.youtube.com/embed/${video.key}`}
                                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                ></iframe>
                                            </div>
                                        </MDBAccordionItem>
                                    ))}
                                </MDBAccordion>
                            </>
                        )}
                        {videos.clips && videos.clips.length > 0 && (
                            <>
                                <h6 className="text-info mt-2">Clips</h6>
                                <MDBAccordion
                                    flush
                                    style={{
                                        minWidth: '100%',
                                    }}
                                >
                                    {videos.clips.map((video, index) => (
                                        <MDBAccordionItem
                                            key={index}
                                            collapseId={index + 1}
                                            headerTitle={video.name}
                                            style={{
                                                backgroundColor:
                                                    'rgba(0, 0, 0, 0.5)',
                                            }}
                                        >
                                            <div className="video-container">
                                                <iframe
                                                    title={video.name}
                                                    src={`https://www.youtube.com/embed/${video.key}`}
                                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                ></iframe>
                                            </div>
                                        </MDBAccordionItem>
                                    ))}
                                </MDBAccordion>
                            </>
                        )}
                        {videos.featurettes && videos.featurettes.length > 0 && (
                            <>
                                <h6 className="text-info mt-2">Destaques</h6>
                                <MDBAccordion
                                    flush
                                    style={{
                                        minWidth: '100%',
                                    }}
                                >
                                    {videos.featurettes.map((video, index) => (
                                        <MDBAccordionItem
                                            key={index}
                                            collapseId={index + 1}
                                            headerTitle={video.name}
                                            style={{
                                                backgroundColor:
                                                    'rgba(0, 0, 0, 0.5)',
                                            }}
                                        >
                                            <div className="video-container">
                                                <iframe
                                                    title={video.name}
                                                    src={`https://www.youtube.com/embed/${video.key}`}
                                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                ></iframe>
                                            </div>
                                        </MDBAccordionItem>
                                    ))}
                                </MDBAccordion>
                            </>
                        )}
                        {videos.behindTheScenes &&
                            videos.behindTheScenes.length > 0 && (
                                <>
                                    <h6 className="text-info mt-2">
                                        Bastidores
                                    </h6>
                                    <MDBAccordion
                                        flush
                                        style={{
                                            minWidth: '100%',
                                        }}
                                    >
                                        {videos.behindTheScenes.map(
                                            (video, index) => (
                                                <MDBAccordionItem
                                                    key={index}
                                                    collapseId={index + 1}
                                                    headerTitle={video.name}
                                                    style={{
                                                        backgroundColor:
                                                            'rgba(0, 0, 0, 0.5)',
                                                    }}
                                                >
                                                    <div className="video-container">
                                                        <iframe
                                                            title={video.name}
                                                            src={`https://www.youtube.com/embed/${video.key}`}
                                                            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                        ></iframe>
                                                    </div>
                                                </MDBAccordionItem>
                                            )
                                        )}
                                    </MDBAccordion>
                                </>
                            )}
                        {videos.others && videos.others.length > 0 && (
                            <>
                                <h6 className="text-info mt-2">Outros</h6>
                                <MDBAccordion
                                    flush
                                    style={{
                                        minWidth: '100%',
                                    }}
                                >
                                    {videos.others.map((video, index) => (
                                        <MDBAccordionItem
                                            key={index}
                                            collapseId={index + 1}
                                            headerTitle={video.name}
                                            style={{
                                                backgroundColor:
                                                    'rgba(0, 0, 0, 0.5)',
                                            }}
                                        >
                                            <div className="video-container">
                                                <iframe
                                                    title={video.name}
                                                    src={`https://www.youtube.com/embed/${video.key}`}
                                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                ></iframe>
                                            </div>
                                        </MDBAccordionItem>
                                    ))}
                                </MDBAccordion>
                            </>
                        )}
                    </>
                )}
                {!videos && <p> Nenhum vídeo encontrado</p>}
            </MDBContainer>
        </>
    );
};

export default Videos;
