import '../styles/main.css';
import {
    MDBContainer,
    MDBNavbar,
    MDBNavbarBrand,
    MDBIcon,
    MDBInputGroup,
} from 'mdb-react-ui-kit';
import AsyncSelect from 'react-select/async';
import { getMoviesByKeyword } from '../scripts/requests';
import { useNavigate } from 'react-router-dom';

const CustomNavbar = () => {
    let navigate = useNavigate();
    var movieTimeout: NodeJS.Timeout;

    const getMoviesSearchArray = async (keyword: string) => {
        if (keyword.length < 2) {
            return [];
        }

        let movies = [
            { value: 0, label: `Pesquisar por "${keyword}"...` },
            ...(await getMoviesByKeyword(keyword)),
        ];

        return movies;
    };

    const movieOptions = (keyword: string) =>
        new Promise<any[]>((resolve) => {
            clearTimeout(movieTimeout);

            movieTimeout = setTimeout(() => {
                resolve(getMoviesSearchArray(keyword));
            }, 500);
        });

    return (
        <MDBNavbar className="sticky-top darkBackground dark">
            <MDBContainer fluid>
                <MDBNavbarBrand className="text-info ms-3" href="/">
                    <MDBIcon fas icon="film" className="me-2" />
                    Movie Finder{' '}
                    <span className="text-white ms-2">do Gug√£o</span>
                </MDBNavbarBrand>
                <MDBInputGroup tag="form" className="d-flex w-auto mb-1">
                    <AsyncSelect
                        placeholder={'Pesquisar...'}
                        className="pt-3"
                        noOptionsMessage={() => 'Nada encontrado'}
                        cacheOptions
                        loadOptions={movieOptions}
                        defaultOptions
                        loadingMessage={() => 'Buscando...'}
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 11,
                                minWidth: '300px',
                                maxWidth: '500px',
                            }),
                            menu: (base) => ({
                                ...base,
                                color: 'white',
                                backgroundColor: 'black',
                                border: '2px solid rgb(57, 192, 237)',
                            }),
                            noOptionsMessage: (base) => ({
                                ...base,
                                color: 'white',
                            }),
                            placeholder: (base) => ({
                                ...base,
                                color: 'white',
                            }),
                            option: (base) => ({
                                ...base,
                                ':hover': {
                                    backgroundColor: 'rgba(57, 192, 237, 0.5)',
                                },
                            }),
                        }}
                        onChange={(movie) => {
                            window.location.replace(
                                `http://localhost:3000/movie/${movie.value}`
                            );
                        }}
                    />
                </MDBInputGroup>
            </MDBContainer>
        </MDBNavbar>
    );
};

export default CustomNavbar;
