import '../../../styles/main.css';
import {
    MDBContainer,
    MDBBtnGroup,
    MDBAccordion,
    MDBAccordionItem,
    MDBIcon,
    MDBRadio,
} from 'mdb-react-ui-kit';
import { useEffect, useState } from 'react';

interface IReviewsProps {
    movieId: number;
}

const Reviews = (props: IReviewsProps) => {
    const [reviews, setReviews] = useState<IReview[] | null>(null);

    return (
        <>
            <MDBContainer className="mt-4" style={{ height: 'auto' }}>
                <h2 className="text-info">Reviews</h2>
                <MDBBtnGroup className="mb-3">
                    <MDBRadio
                        id="ptReviewButton"
                        name="reviewLanguage"
                        className=""
                        wrapperClass="me-2 transparentBG"
                        onClick={() => reviewsLanguageClick('pt-BR')}
                        btn
                        wrapperTag="span"
                        label="Português"
                        defaultChecked
                    />
                    <MDBRadio
                        id="enReviewButton"
                        name="reviewLanguage"
                        onClick={() => reviewsLanguageClick('en-US')}
                        wrapperTag="span"
                        btn
                        label="Inglês"
                    />
                </MDBBtnGroup>
                <>
                    {reviews && (
                        <MDBAccordion flush style={{ borderRadius: '5px' }}>
                            {reviews.map((r, index) => (
                                <MDBAccordionItem
                                    key={index}
                                    collapseId={index + 1}
                                    className="text-white"
                                    headerTitle={
                                        <>
                                            {r.author_details.rating && (
                                                <div className="text-warning me-2">
                                                    <MDBIcon
                                                        icon="star"
                                                        className="me-2"
                                                    />
                                                    {r.author_details.rating}
                                                </div>
                                            )}
                                            <span>
                                                {r.author} (
                                                {formatDate(r.created_at)})
                                            </span>
                                        </>
                                    }
                                    style={{
                                        backgroundColor: 'rgba(0, 0, 0, 0.5)',
                                    }}
                                >
                                    {r.content}
                                </MDBAccordionItem>
                            ))}
                        </MDBAccordion>
                    )}
                    {!reviews && <p> Nenhuma review encontrada</p>}
                </>
            </MDBContainer>
        </>
    );
};

export default Reviews;
