import '../styles/main.css';
import {
    MDBContainer,
    MDBNavbar,
    MDBNavbarBrand,
    MDBIcon,
    MDBNavbarNav,
    MDBInputGroup,
    MDBBtn,
} from 'mdb-react-ui-kit';
import AsyncSelect from 'react-select/async';
import { getMoviesByKeyword } from '../scripts/requests';
import { useNavigate } from 'react-router-dom';

const CustomNavbar = () => {
    let navigate = useNavigate();
    var movieTimeout: NodeJS.Timeout;

    const getMoviesSearchArray = async (keyword: string) => {
        if (keyword.length < 2) {
            return [];
        }

        let movies = [
            { value: 0, label: `Pesquisar por "${keyword}"...` },
            ...(await getMoviesByKeyword(keyword)),
        ];

        return movies;
    };

    const movieOptions = (keyword: string) =>
        new Promise<any[]>((resolve) => {
            clearTimeout(movieTimeout);

            movieTimeout = setTimeout(() => {
                resolve(getMoviesSearchArray(keyword));
            }, 500);
        });

    return (
        <>
            <MDBNavbar className="sticky-top darkBackground dark">
                <MDBContainer className="ms-2 me-0">
                    <MDBNavbarBrand className="text-info" href="/">
                        <MDBIcon fas icon="film" className="me-2" />
                        Movie Finder{' '}
                        <span className="text-white ms-2">do Gug√£o</span>
                    </MDBNavbarBrand>
                    <MDBNavbarNav
                        right
                        fullWidth={false}
                        className="mb-2 mb-lg-0"
                    >
                        <form className="d-flex input-group w-auto"></form>
                    </MDBNavbarNav>
                </MDBContainer>
            </MDBNavbar>
            <MDBNavbar light bgColor="light">
                <MDBContainer fluid>
                    <MDBNavbarBrand>Navbar</MDBNavbarBrand>
                    <MDBInputGroup tag="form" className="d-flex w-auto mb-3">
                        <AsyncSelect
                            placeholder={'Pesquisar...'}
                            className="mb-1"
                            noOptionsMessage={() => 'Nada encontrado'}
                            cacheOptions
                            loadOptions={movieOptions}
                            defaultOptions
                            loadingMessage={() => 'Buscando...'}
                            styles={{
                                container: (base) => ({
                                    ...base,
                                    zIndex: 11,
                                }),
                                menu: (base) => ({
                                    ...base,
                                    color: 'white',
                                    backgroundColor: 'black',
                                    border: '2px solid rgb(57, 192, 237)',
                                }),
                                noOptionsMessage: (base) => ({
                                    ...base,
                                    color: 'white',
                                }),
                                placeholder: (base) => ({
                                    ...base,
                                    color: 'white',
                                }),
                                option: (base) => ({
                                    ...base,
                                    ':hover': {
                                        backgroundColor:
                                            'rgba(57, 192, 237, 0.5)',
                                    },
                                }),
                            }}
                            onChange={(movie) =>
                                navigate(`movie/${movie.value}`)
                            }
                        />
                    </MDBInputGroup>
                </MDBContainer>
            </MDBNavbar>
        </>
    );
};

export default CustomNavbar;
