import '../styles/main.css';
import '../styles/movie-grid.css';
import {
    MDBRow,
    MDBCol,
    MDBContainer,
    MDBSpinner,
    MDBPagination,
    MDBPaginationItem,
    MDBPaginationLink,
} from 'mdb-react-ui-kit';
import Movie from './movie';
import { useTypedSelector } from '../hooks/useTypedSelector';
import Alert from 'react-bootstrap/Alert';
import { useState } from 'react';

const MovieGrid = () => {
    const [page, setPage] = useState(1);

    const { data, error, loading } = useTypedSelector(
        (state) => state.repositories
    );

    const isActive = (thisPage: number): boolean => {
        console.log(thisPage, page);
        return thisPage === page;
    };

    const page1 = (): number => {
        if (page - 2 <= 1) {
            return 1;
        }

        return page - 2;
    };
    const page2 = (): number => {
        if (page - 1 <= 2) {
            return 2;
        }

        return page - 1;
    };
    const page3 = (): number => {
        if (page <= 3) {
            return 3;
        }

        return page;
    };
    const page4 = (): number => {
        if (page + 1 <= 4) {
            return 4;
        }

        return page + 1;
    };
    const page5 = (): number => {
        if (page + 2 <= 5) {
            return 5;
        }

        return page + 2;
    };

    return (
        <div>
            {error && <Alert variant="danger">{error}</Alert>}
            {loading && (
                <div className="centralizar">
                    <MDBSpinner role="status" color="dark">
                        <span className="visually-hidden">Loading...</span>
                    </MDBSpinner>
                </div>
            )}
            {!error && !loading && (
                <MDBContainer fluid>
                    <MDBRow className="col-12  g-3 m-0">
                        {data.map((movie) => (
                            <MDBCol key={movie.id}>
                                <Movie
                                    name={movie.title}
                                    imageSRC={movie.poster_path}
                                    releaseDate={movie.release_date}
                                    budget={movie.budget}
                                    revenue={movie.revenue}
                                    rating={movie.vote_average}
                                    tagline={movie.tagline}
                                />
                            </MDBCol>
                        ))}
                    </MDBRow>
                    {data.length > 0 && (
                        <MDBRow className="mt-4">
                            <MDBPagination center>
                                <MDBPaginationItem
                                    onClick={() =>
                                        setPage(page > 1 ? page - 1 : page)
                                    }
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        a
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page1())}
                                    onClick={() => setPage(page1())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page1()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page2())}
                                    onClick={() => setPage(page2())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page2()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page3())}
                                    onClick={() => setPage(page3())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page3()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page4())}
                                    onClick={() => setPage(page4())}
                                    className="selectedPage"
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page4()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page5())}
                                    onClick={() => setPage(page5())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page5()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    onClick={() => setPage(page + 1)}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        a
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                            </MDBPagination>
                        </MDBRow>
                    )}
                </MDBContainer>
            )}
        </div>
    );
};

export default MovieGrid;
