const axios = require('axios');

interface IGenero {
    id: number;
    name: string;
}

export interface IGeneros extends Array<IGenero> {}

export const getGeneros = async (): Promise<IGeneros> => {
    const generos: IGeneros = [];

    await axios
        .get('http://localhost:3001/generos')
        .then((response: any) => {
            response.data.genres.forEach((g: IGenero) => {
                generos.push(g);
            });
        })
        .catch((error: any) => {
            console.log(error);
        });

    const customSort = (a: IGenero, b: IGenero) => {
        if (a.name < b.name) {
            return -1;
        }
        if (a.name > b.name) {
            return 1;
        }
        return 0;
    };

    generos.sort(customSort);

    return generos;
};

interface IMovie {
    poster_path: string;
    adult: boolean;
    overview: string;
    release_date: string;
    genres: Array<number>;
    id: number;
    original_title: string;
    title: string;
    backdrop_path: string;
    vote_average: number;
    budget: number;
    revenue: number;
    runtime: number;
    tagline: string;
}

export interface IMovies extends Array<IMovie> {
    total_pages: number;
}

export const getMovies = async (
    genero: string,
    adult: boolean,
    page: number
): Promise<IMovies> => {
    const movies: IMovies = [{ total_pages: 0 }];

    await axios
        .get('http://localhost:3001/descobrir', {
            params: {
                genero: genero,
                adult: adult,
                page: page,
            },
        })
        .then((response: any) => {
            if (response.data) {
                response.data.movies.forEach((m: IMovie) => {
                    movies.push(m);
                });
            }
        })
        .catch((error: any) => {
            console.log(error);
        });

    return movies;
};
