import '../styles/filter.css';
import '../styles/main.css';
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardText,
    MDBSwitch,
    MDBIcon,
    MDBRow,
    MDBCol,
    MDBTooltip,
} from 'mdb-react-ui-kit';
import { useEffect, useState } from 'react';
import {
    IGeneros,
    getGeneros,
    getMoviesByKeyword,
    getPeopleByKeyword,
} from '../scripts/requests';
import { useActions } from '../hooks/useActions';
import { IFilter } from './content';
import Select, { MultiValue, SingleValue } from 'react-select';
import AsyncSelect from 'react-select/async';

interface IFiltro {
    filterState: IFilter;
    setFilterState: React.Dispatch<React.SetStateAction<IFilter>>;
}

interface ISelectOption {
    value: number;
    label: string;
}

const Filter: React.FC<IFiltro> = ({ filterState, setFilterState }) => {
    const [generos, setGeneros] = useState<IGeneros>([]);
    const [filmesPesquisa, setFilmesPesquisa] = useState<
        {
            id: number;
            title: string;
        }[]
    >([]);
    const [adulto, setAdulto] = useState(false);
    const [genero, setGenero] = useState('');
    const [sort, setSort] = useState('4');
    const { searchMovies } = useActions();

    useEffect(() => {
        const fetchGeneros = async () => {
            setGeneros(await getGeneros());
        };

        fetchGeneros();
    }, []);

    const buscarClick = async () => {
        searchMovies(genero, adulto, 1, sort);

        setFilterState({
            genero: genero,
            adulto: adulto,
            sort: sort,
        });
    };

    const onGeneroChange = (values: MultiValue<ISelectOption>) => {
        var generosText = '';

        values.forEach((e) => {
            if (generosText !== '') {
                generosText += ',';
            }

            generosText += e.value;
        });

        setGenero(generosText);
        console.log(generosText);
    };

    const onSortChange = (
        value: SingleValue<{ value: string; label: string }>
    ) => {
        if (value) {
            setSort(value.value);
        }
    };

    const getGeneroOptions = () => {
        var generosOptions: ISelectOption[] = [];

        generos.map((genero) =>
            generosOptions.push({ value: genero.id, label: genero.name })
        );

        return generosOptions;
    };

    const sortOptions = [
        { value: '0', label: 'Avaliação (maiores primeiro)' },
        { value: '1', label: 'Avaliação (menores primeiro)' },
        {
            value: '2',
            label: 'Data de lançamento (mais recentes primeiro)',
        },
        { value: '3', label: 'Data de lançamento (mais antigos primeiro)' },
        { value: '4', label: 'Popularidade atual' },
        { value: '5', label: 'Popularidade histórica' },
        { value: '6', label: 'Receita gerada (maiores primeiro)' },
        { value: '7', label: 'Receita gerada (menores primeiro)' },
    ];

    var movieTimeout: NodeJS.Timeout;

    const getMoviesSearchArray = async (keyword: string) => {
        if (keyword.length < 2) {
            return [];
        }

        let movies = [
            { value: 0, label: `Pesquisar por "${keyword}"...` },
            ...(await getMoviesByKeyword(keyword)),
        ];

        return movies;
    };

    const movieOptions = (keyword: string) =>
        new Promise<any[]>((resolve) => {
            clearTimeout(movieTimeout);

            movieTimeout = setTimeout(() => {
                resolve(getMoviesSearchArray(keyword));
            }, 500);
        });

    var peopleTimeout: NodeJS.Timeout;

    const peopleOptions = (keyword: string) =>
        new Promise<any[]>((resolve) => {
            clearTimeout(peopleTimeout);

            peopleTimeout = setTimeout(() => {
                resolve(getPeopleByKeyword(keyword));
            }, 500);
        });

    return (
        <div>
            <MDBCard className="p-1 m-3 filterCard" background="dark">
                <MDBCardBody>
                    <MDBRow>
                        <MDBCol md="6">
                            <AsyncSelect
                                placeholder={'Pesquisar...'}
                                noOptionsMessage={() => 'Nada encontrado'}
                                cacheOptions
                                loadOptions={movieOptions}
                                defaultOptions
                                loadingMessage={() => 'Buscando...'}
                                styles={{
                                    container: (base) => ({
                                        ...base,
                                        zIndex: 11,
                                    }),
                                }}
                            />
                        </MDBCol>
                        <MDBCol>
                            <MDBTooltip
                                tag="a"
                                wrapperProps={{ href: '#' }}
                                placement="right"
                                title="Pesquise o nome de algum filme"
                            >
                                <MDBIcon
                                    color="white"
                                    fas
                                    icon="info-circle"
                                    className="m-0"
                                />
                            </MDBTooltip>
                        </MDBCol>
                    </MDBRow>
                    <MDBCardText className="m-2 text-white">
                        Pesquisa avançada
                    </MDBCardText>
                    <Select
                        onChange={(values) => onGeneroChange(values)}
                        className="mt-2"
                        placeholder={'Gênero'}
                        noOptionsMessage={() => 'Nada encontrado'}
                        isMulti
                        options={getGeneroOptions()}
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 10,
                            }),
                        }}
                    />
                    <MDBCol>
                        <MDBTooltip
                            tag="a"
                            wrapperProps={{ href: '#' }}
                            placement="right"
                            title="Pesquise o nome de algum filme"
                        >
                            <MDBIcon
                                color="white"
                                fas
                                icon="info-circle"
                                className="m-0"
                            />
                        </MDBTooltip>
                    </MDBCol>
                    <Select
                        onChange={(value) => onSortChange(value)}
                        className="mt-2"
                        placeholder={'Ordenação'}
                        noOptionsMessage={() => 'Nada encontrado'}
                        defaultValue={[sortOptions[4]]}
                        options={sortOptions}
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 9,
                            }),
                        }}
                    />
                    <AsyncSelect
                        placeholder={'Pessoas'}
                        className="mt-2"
                        noOptionsMessage={() => 'Nada encontrado'}
                        cacheOptions
                        loadOptions={peopleOptions}
                        defaultOptions
                        loadingMessage={() => 'Buscando...'}
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 8,
                            }),
                        }}
                    />
                    <div className="text-white mt-3">
                        <MDBSwitch
                            id="adult"
                            className="me-3"
                            label="Mostrar filmes adultos (conteúdo sexual)"
                            onChange={() => setAdulto(!adulto)}
                            checked={adulto}
                        />
                    </div>
                    <div className="centralizar mt-3">
                        <MDBBtn
                            type="submit"
                            color="success"
                            className="me-2"
                            onClick={buscarClick}
                        >
                            Buscar
                        </MDBBtn>
                        <MDBBtn type="submit" color="light" className="ms-2">
                            Estou com sorte
                        </MDBBtn>
                    </div>
                </MDBCardBody>
            </MDBCard>
        </div>
    );
};

export default Filter;
