import '../../styles/main.css';
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBIcon,
    MDBCard,
    MDBCardImage,
    MDBCardBody,
    MDBCardText,
    MDBCollapse,
} from 'mdb-react-ui-kit';
import { ICastMembers } from '../../../scripts/requests';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

interface ICastMembersProps {
    castMembers: ICastMembers | null;
}

const CastMembers = (props: ICastMembersProps) => {
    const [showMore, setShowMore] = useState(false);

    const toggleShow = () => setShowMore(!showMore);

    useEffect(() => {}, [props.movieId]);

    return (
        <>
            {castMembers?.cast && castMembers.cast.length > 0 && (
                <MDBContainer className="mt-3">
                    <h2 className="text-info">Atores</h2>
                    <MDBRow className="ms-0 me-0">
                        {castMembers &&
                            castMembers.cast.map((c, index) => (
                                <MDBCol key={index} sm="2" className="p-1 ps-0">
                                    {index < 6 && (
                                        <Link
                                            to={`/MovieFinder?pessoaParam=${c.id}`}
                                            onClick={window.location.reload}
                                        >
                                            <MDBCard
                                                alignment="center"
                                                className="popup"
                                            >
                                                {c.profile_path && (
                                                    <MDBCardImage
                                                        src={`https://image.tmdb.org/t/p/w500${c.profile_path}`}
                                                        alt={`foto de ${c.name}`}
                                                        position="top"
                                                    />
                                                )}
                                                <MDBCardBody className="p-1">
                                                    <MDBCardText>
                                                        <small>
                                                            <span className="text-white m-0">
                                                                {c.name}
                                                            </span>
                                                            <br />
                                                            <span className="text-info m-0">
                                                                {c.character}
                                                            </span>
                                                        </small>
                                                    </MDBCardText>
                                                </MDBCardBody>
                                            </MDBCard>
                                        </Link>
                                    )}
                                </MDBCol>
                            ))}
                    </MDBRow>

                    {castMembers.cast.length > 6 && (
                        <>
                            <MDBCollapse show={showMore}>
                                <MDBRow className="ms-0 me-0">
                                    {castMembers &&
                                        castMembers.cast.map((c, index) => (
                                            <MDBCol
                                                key={index}
                                                sm="2"
                                                className="p-1 ps-0"
                                            >
                                                {index >= 6 && (
                                                    <Link
                                                        to={`/MovieFinder?pessoaParam=${c.id}`}
                                                        onClick={
                                                            window.location
                                                                .reload
                                                        }
                                                    >
                                                        <MDBCard
                                                            alignment="center"
                                                            className="popup"
                                                        >
                                                            {c.profile_path && (
                                                                <MDBCardImage
                                                                    src={`https://image.tmdb.org/t/p/w500${c.profile_path}`}
                                                                    alt={`foto de ${c.name}`}
                                                                    position="top"
                                                                />
                                                            )}
                                                            <MDBCardBody className="p-1">
                                                                <MDBCardText>
                                                                    <small>
                                                                        <span className="text-white m-0">
                                                                            {
                                                                                c.name
                                                                            }
                                                                        </span>
                                                                        <br />
                                                                        <span className="text-info m-0">
                                                                            {
                                                                                c.character
                                                                            }
                                                                        </span>
                                                                    </small>
                                                                </MDBCardText>
                                                            </MDBCardBody>
                                                        </MDBCard>
                                                    </Link>
                                                )}
                                            </MDBCol>
                                        ))}
                                </MDBRow>
                            </MDBCollapse>
                            <span className="fake-link" onClick={toggleShow}>
                                {showMore && (
                                    <span>
                                        Mostrar menos
                                        <MDBIcon
                                            fas
                                            icon="chevron-up"
                                            className="ms-2"
                                        />
                                    </span>
                                )}
                                {!showMore && (
                                    <span>
                                        Mostrar mais
                                        <MDBIcon
                                            fas
                                            icon="chevron-down"
                                            className="ms-2"
                                        />
                                    </span>
                                )}
                            </span>
                        </>
                    )}
                </MDBContainer>
            )}
        </>
    );
};

export default CastMembers;
