import '../styles/content.css';
import '../styles/main.css';
import Filter from './filter';
import MovieGrid from './movie-grid';
import { useState } from 'react';
import { useTypedSelector } from '../hooks/useTypedSelector';
import { useEffect } from 'react';
import { useActions } from '../hooks/useActions';

export interface IFilter {
    genero: string;
    adulto: boolean;
    sort: string;
    pessoa: string;
    dataInicial: Date | null;
    dataFinal: Date | null;
    voteCount: number;
    tipoBusca: string;
}

const Content = () => {
    const { searchMovies } = useActions();
    const [filterState, setfilterState] = useState<IFilter>({
        genero: '',
        adulto: false,
        sort: '4',
        pessoa: '',
        dataInicial: null,
        dataFinal: null,
        voteCount: 100,
        tipoBusca: '',
    });

    const { data } = useTypedSelector((state) => state.repositories);

    useEffect(() => {
        searchMovies('', false, 1, '4', '', null, null, 100);

        setfilterState({
            genero: '',
            adulto: false,
            sort: '4',
            pessoa: '',
            dataInicial: null,
            dataFinal: null,
            voteCount: 100,
            tipoBusca: 'Tendências de hoje',
        });

        console.log(filterState.tipoBusca);
    }, []);

    return (
        <div className="content-main">
            <div className="bg-image">
                <img
                    className="fullscreenImage"
                    src={
                        'https://image.tmdb.org/t/p/original/sfw4m2tOgQRzhF6VXxaXGfd1vX.jpg'
                    }
                    alt="plano de fundo"
                    style={{
                        filter: 'brightness(0.2)',
                        zIndex: -1,
                    }}
                />
            </div>
            <div className="content">
                <Filter
                    filterState={filterState}
                    setFilterState={setfilterState}
                ></Filter>
                {filterState.tipoBusca && (
                    <h2
                        className="centralizar text-info"
                        style={{ zIndex: 20, height: '50px', width: '100%' }}
                    >
                        {filterState.tipoBusca}
                    </h2>
                )}
                {filterState.tipoBusca == 'Pesquisa personalizada' &&
                    data.total_results > 0 && (
                        <span className="centralizar text-white">
                            {data.total_results} resultados
                        </span>
                    )}
                <MovieGrid
                    filterState={filterState}
                    setFilterState={setfilterState}
                ></MovieGrid>
            </div>
        </div>
    );
};

export default Content;
