import '../styles/content.css';
import '../styles/main.css';
import Filter from './filter';
import MovieGrid from './movie-grid';
import { useState } from 'react';
import { useTypedSelector } from '../hooks/useTypedSelector';

export interface IFilter {
    genero: string;
    adulto: boolean;
    sort: string;
    pessoa: string;
    dataInicial: Date | null;
    dataFinal: Date | null;
    voteCount: number;
}

const Content = () => {
    const [filterState, setfilterState] = useState<IFilter>({
        genero: '',
        adulto: false,
        sort: '4',
        pessoa: '',
        dataInicial: null,
        dataFinal: null,
        voteCount: 100,
    });

    const { data } = useTypedSelector((state) => state.repositories);

    return (
        <div className="content-main">
            <div className="bg-image">
                {
                    /*data &&
                    data.movie_details &&
                    data.movie_details.length > 0 &&
                    data.movie_details[0].backdrop_path &&*/ <img
                        className="fullscreenImage"
                        src={
                            'https://image.tmdb.org/t/p/original/sfw4m2tOgQRzhF6VXxaXGfd1vX.jpg' /*`https://image.tmdb.org/t/p/original${
                                data.movie_details[
                                    Math.floor(
                                        Math.random() *
                                            (data.movie_details.length + 1)
                                    )
                                ].backdrop_path
                            }`*/
                        }
                        alt="plano de fundo"
                        style={{
                            filter: 'brightness(0.2)',
                        }}
                    />
                }
            </div>
            <div className="content">
                <Filter
                    filterState={filterState}
                    setFilterState={setfilterState}
                ></Filter>
                {data.total_results > 0 && (
                    <span className="centralizar text-white">
                        {data.total_results} resultados
                    </span>
                )}
                <MovieGrid
                    filterState={filterState}
                    setFilterState={setfilterState}
                ></MovieGrid>
            </div>
        </div>
    );
};

export default Content;
