import '../styles/main.css';
import {
    MDBContainer,
    MDBNavbar,
    MDBNavbarBrand,
    MDBIcon,
} from 'mdb-react-ui-kit';

const CustomNavbar = () => {
    return (
        <MDBNavbar className="sticky-top darkBackground dark">
            <MDBContainer className="ms-2">
                <MDBNavbarBrand className="text-info" href="/">
                    <MDBIcon fas icon="film" className="me-2" />
                    Movie Finder{' '}
                    <span className="text-white ms-2">do Gug√£o</span>
                    <AsyncSelect
                        placeholder={'Pesquisar...'}
                        className="mb-1"
                        noOptionsMessage={() => 'Nada encontrado'}
                        cacheOptions
                        loadOptions={movieOptions}
                        defaultOptions
                        loadingMessage={() => 'Buscando...'}
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 11,
                            }),
                            menu: (base) => ({
                                ...base,
                                color: 'white',
                                backgroundColor: 'black',
                                border: '2px solid rgb(57, 192, 237)',
                            }),
                            noOptionsMessage: (base) => ({
                                ...base,
                                color: 'white',
                            }),
                            placeholder: (base) => ({
                                ...base,
                                color: 'white',
                            }),
                            option: (base) => ({
                                ...base,
                                ':hover': {
                                    backgroundColor: 'rgba(57, 192, 237, 0.5)',
                                },
                            }),
                        }}
                        onChange={(movie) => navigate(`movie/${movie.value}`)}
                    />
                </MDBNavbarBrand>
            </MDBContainer>
        </MDBNavbar>
    );
};

export default CustomNavbar;
