import '../../styles/main.css';
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBBtnGroup,
    MDBBtn,
    MDBAccordion,
    MDBAccordionItem,
    MDBIcon,
    MDBRadio,
    MDBTooltip,
    MDBSpinner,
} from 'mdb-react-ui-kit';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import {
    IMovie,
    getMovieDetails,
    IReview,
    getMovieReviews,
    IKeyword,
    getMovieKeywords,
    ICastMembers,
    getCastMembers,
} from '../../scripts/requests';
import { Link } from 'react-router-dom';
import WatchProviders from './content/watch-providers';
import CastMembers from './content/cast-members';
import SimilarMovies from './content/similar-movies';
import Videos from './content/videos';

const MoviePage = () => {
    const [movie, setMovie] = useState<IMovie | null | undefined>(null);
    const [reviews, setReviews] = useState<IReview[] | null>(null);
    const [movieKeywords, setMovieKeywords] = useState<IKeyword[] | null>(null);
    const [castMembers, setCastMembers] = useState<ICastMembers | null>(null);
    const { movieID } = useParams();

    const isNumber = (str: string): boolean => {
        if (typeof str !== 'string') {
            return false;
        }

        if (str.trim() === '') {
            return false;
        }

        return !Number.isNaN(Number(str));
    };

    const getMovieId = (): number => {
        if (!movieID || !isNumber(movieID)) {
            return 0;
        }

        return parseInt(movieID);
    };

    const fetchReviews = async (language: string) => {
        const mId = getMovieId();

        if (mId > 0) {
            setReviews(await getMovieReviews(mId, language));
        }
    };

    useEffect(() => {
        const mId = getMovieId();

        if (mId > 0) {
            const fetchMovieDetails = async () => {
                setMovie(await getMovieDetails(mId));
            };

            const fetchMovieKeywords = async () => {
                setMovieKeywords(await getMovieKeywords(mId));
            };

            const fetchMovieCast = async () => {
                setCastMembers(await getCastMembers(mId));
            };

            fetchMovieDetails();
            fetchReviews('pt-BR');
            fetchMovieCast();
            fetchMovieKeywords();
        }
    }, [movieID]);

    useEffect(() => {
        // Garante que vai mostrar a parte de cima da página
        window.scrollTo(0, 0);
    }, []);

    const reviewsLanguageClick = (language: string) => {
        fetchReviews(language);
    };

    const padTo2Digits = (num: number) => {
        return num.toString().padStart(2, '0');
    };

    const formatDate = (date: Date) => {
        let d = new Date(date);

        return [
            padTo2Digits(d.getDate()),
            padTo2Digits(d.getMonth() + 1),
            d.getFullYear(),
        ].join('/');
    };

    const formatDateStr = (date: string) => {
        let datePart: RegExpMatchArray | null = date.match(/\d+/g);
        let year = '';
        let month = '';
        let day = '';

        if (datePart) {
            year = datePart[0];
            month = datePart[1];
            day = datePart[2];
        }

        return day + '/' + month + '/' + year;
    };

    const priceSplitter = (number: number) =>
        number && number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.');

    let languageNames = new Intl.DisplayNames(['pt-BR'], { type: 'language' });

    const convertMinutes = (totalMinutes: number): string => {
        const hours = Math.floor(totalMinutes / 60);
        const minutes = totalMinutes % 60;

        if (hours === 0) {
            return `${minutes}m`;
        }

        return `${hours}h e ${minutes}m`;
    };

    return (
        <>
            <div className="content-main">
                <div className="bg-image">
                    {movie && movie.backdrop_path && (
                        <img
                            className="fullscreenImage"
                            src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
                            alt="plano de fundo"
                            style={{
                                filter: 'brightness(0.2)',
                            }}
                        />
                    )}
                </div>
                <div
                    className="content moviepage"
                    style={{
                        /*backgroundColor: 'rgba(0, 0, 0, 0.75)',*/
                        zIndex: 10,
                    }}
                >
                    <div className="text-white">
                        {movie && !movie.adult && (
                            <div className="text-white m-4">
                                <MDBContainer>
                                    <MDBRow center>
                                        <MDBCol size="md-2 me-4 centralizar">
                                            {movie.poster_path && (
                                                <img
                                                    src={
                                                        'https://image.tmdb.org/t/p/w500' +
                                                        movie.poster_path
                                                    }
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt={`Poster de ${movie.title}`}
                                                    style={{
                                                        maxHeight: '275px',
                                                    }}
                                                />
                                            )}
                                            {!movie.poster_path && (
                                                <img
                                                    src="/PosterNotFound.jpg"
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt="Poster não encontrado"
                                                    style={{
                                                        maxHeight: '275px',
                                                    }}
                                                />
                                            )}
                                        </MDBCol>
                                        <MDBCol size="md">
                                            <h1 className="text-info">
                                                <span>{movie.title}</span>
                                            </h1>
                                            {movie.tagline && (
                                                <h4
                                                    style={{
                                                        fontStyle: 'italic',
                                                    }}
                                                >
                                                    "{movie.tagline}"
                                                </h4>
                                            )}
                                            <p>{movie.overview}</p>
                                            <MDBRow>
                                                <MDBCol
                                                    sm="auto"
                                                    className="mb-1"
                                                >
                                                    <MDBTooltip
                                                        tag="div"
                                                        placement="top"
                                                        wrapperProps={{
                                                            className:
                                                                'fake-link',
                                                        }}
                                                        title={`${movie.vote_count} avaliações`}
                                                    >
                                                        <span className="text-warning bold">
                                                            <MDBIcon
                                                                far
                                                                icon="star"
                                                                className="me-2"
                                                            />
                                                            {movie.vote_average.toFixed(
                                                                1
                                                            )}
                                                        </span>
                                                    </MDBTooltip>
                                                </MDBCol>
                                                <MDBCol
                                                    sm="auto"
                                                    className="mb-1"
                                                >
                                                    <span className="text-info bold">
                                                        <MDBIcon
                                                            fas
                                                            icon="calendar-alt"
                                                            className="me-2"
                                                        />
                                                        {formatDateStr(
                                                            movie.release_date
                                                        )}
                                                    </span>
                                                </MDBCol>
                                                <MDBCol
                                                    sm="auto"
                                                    className="mb-1"
                                                >
                                                    <span className="text-info bold">
                                                        <MDBIcon
                                                            far
                                                            icon="clock"
                                                            className="me-2"
                                                        />
                                                        {convertMinutes(
                                                            movie.runtime
                                                        )}
                                                    </span>
                                                </MDBCol>
                                                {castMembers &&
                                                    castMembers.directors && (
                                                        <MDBCol
                                                            sm="auto"
                                                            className="mb-1"
                                                        >
                                                            <span className="text-white me-2">
                                                                {castMembers.directors.includes(
                                                                    ', '
                                                                )
                                                                    ? 'Diretores:'
                                                                    : 'Diretor:'}
                                                            </span>
                                                            <span className="text-info bold">
                                                                {
                                                                    castMembers.directors
                                                                }
                                                            </span>
                                                        </MDBCol>
                                                    )}
                                            </MDBRow>
                                            <MDBRow>
                                                <MDBCol
                                                    sm="auto"
                                                    className="mb-1"
                                                >
                                                    <span className="me-2">
                                                        Título original:
                                                    </span>
                                                    <span className="text-info bold">
                                                        {movie.original_title}
                                                    </span>
                                                </MDBCol>
                                                {movie.original_language && (
                                                    <MDBCol
                                                        sm="auto"
                                                        className="mb-1"
                                                    >
                                                        <span className="me-2">
                                                            Linguagem original:
                                                        </span>
                                                        <span
                                                            className="text-info bold"
                                                            style={{
                                                                textTransform:
                                                                    'capitalize',
                                                            }}
                                                        >
                                                            {languageNames.of(
                                                                movie.original_language
                                                            )}
                                                        </span>
                                                    </MDBCol>
                                                )}
                                                <MDBCol
                                                    sm="auto"
                                                    className="mb-1"
                                                >
                                                    <span className="text-white me-2">
                                                        Orçamento:
                                                    </span>
                                                    <span className="text-info bold">
                                                        <MDBIcon
                                                            fas
                                                            icon="dollar-sign"
                                                            className="me-2"
                                                        />
                                                        {movie.budget > 0 &&
                                                            priceSplitter(
                                                                movie.budget
                                                            )}
                                                        {movie.budget <= 0 &&
                                                            '-'}
                                                    </span>
                                                </MDBCol>
                                                <MDBCol
                                                    sm="auto"
                                                    className="mb-1"
                                                >
                                                    <span className="text-white me-2">
                                                        Receita:
                                                    </span>
                                                    <span className="text-info bold">
                                                        <MDBIcon
                                                            fas
                                                            icon="dollar-sign"
                                                            className="me-2"
                                                        />
                                                        {movie.revenue > 0 &&
                                                            priceSplitter(
                                                                movie.revenue
                                                            )}
                                                        {movie.revenue <= 0 &&
                                                            '-'}
                                                    </span>
                                                </MDBCol>
                                            </MDBRow>
                                            <div className="mt-2">
                                                {movie.genres.map(
                                                    (g, index) => (
                                                        <Link
                                                            to={`/MovieFinder?generoParam=${g.id}`}
                                                            onClick={
                                                                window.location
                                                                    .reload
                                                            }
                                                        >
                                                            <MDBBtn
                                                                rounded
                                                                className="m-2 ms-0 transparentBG active"
                                                                color="info"
                                                                key={index}
                                                            >
                                                                {g.name}
                                                            </MDBBtn>
                                                        </Link>
                                                    )
                                                )}
                                            </div>
                                        </MDBCol>
                                    </MDBRow>
                                </MDBContainer>
                                <WatchProviders
                                    movieId={getMovieId()}
                                ></WatchProviders>
                                <CastMembers
                                    castMembers={castMembers}
                                ></CastMembers>
                                <SimilarMovies
                                    movieId={getMovieId()}
                                ></SimilarMovies>
                                <Videos movieId={getMovieId()}></Videos>

                                <MDBContainer
                                    className="mt-4"
                                    style={{ height: 'auto' }}
                                >
                                    <h2 className="text-info">Reviews</h2>
                                    <MDBBtnGroup className="mb-3">
                                        <MDBRadio
                                            id="ptReviewButton"
                                            name="reviewLanguage"
                                            className=""
                                            wrapperClass="me-2 transparentBG"
                                            onClick={() =>
                                                reviewsLanguageClick('pt-BR')
                                            }
                                            btn
                                            wrapperTag="span"
                                            label="Português"
                                            defaultChecked
                                        />
                                        <MDBRadio
                                            id="enReviewButton"
                                            name="reviewLanguage"
                                            onClick={() =>
                                                reviewsLanguageClick('en-US')
                                            }
                                            wrapperTag="span"
                                            btn
                                            label="Inglês"
                                        />
                                    </MDBBtnGroup>
                                    <>
                                        {reviews && (
                                            <MDBAccordion
                                                flush
                                                style={{ borderRadius: '5px' }}
                                            >
                                                {reviews.map((r, index) => (
                                                    <MDBAccordionItem
                                                        key={index}
                                                        collapseId={index + 1}
                                                        className="text-white"
                                                        headerTitle={
                                                            <>
                                                                {r
                                                                    .author_details
                                                                    .rating && (
                                                                    <div className="text-warning me-2">
                                                                        <MDBIcon
                                                                            icon="star"
                                                                            className="me-2"
                                                                        />
                                                                        {
                                                                            r
                                                                                .author_details
                                                                                .rating
                                                                        }
                                                                    </div>
                                                                )}
                                                                <span>
                                                                    {r.author} (
                                                                    {formatDate(
                                                                        r.created_at
                                                                    )}
                                                                    )
                                                                </span>
                                                            </>
                                                        }
                                                        style={{
                                                            backgroundColor:
                                                                'rgba(0, 0, 0, 0.5)',
                                                        }}
                                                    >
                                                        {r.content}
                                                    </MDBAccordionItem>
                                                ))}
                                            </MDBAccordion>
                                        )}
                                        {!reviews && (
                                            <p> Nenhuma review encontrada</p>
                                        )}
                                    </>
                                </MDBContainer>
                                {movieKeywords && (
                                    <MDBContainer
                                        className="mt-4"
                                        style={{ height: 'auto' }}
                                    >
                                        <h2 className="text-info">
                                            Palavras-chave
                                        </h2>
                                        {movieKeywords.map((k, index) => (
                                            <Link
                                                to={`/MovieFinder?keyword=${k.id}`}
                                                onClick={window.location.reload}
                                            >
                                                <MDBBtn
                                                    rounded
                                                    className="m-2 ms-0 transparentBG active"
                                                    color="info"
                                                    key={index}
                                                >
                                                    {k.name}
                                                </MDBBtn>
                                            </Link>
                                        ))}
                                    </MDBContainer>
                                )}
                            </div>
                        )}
                    </div>
                    {movie === null && (
                        <div className="centralizar text-white mt-5 pt-5">
                            <MDBSpinner role="status" color="white">
                                <span className="visually-hidden">
                                    Carregando...
                                </span>
                            </MDBSpinner>
                        </div>
                    )}
                    {movie === undefined && (
                        <h3 className="centralizar text-white mt-5 pt-5">
                            404 - Filme não encontrado
                        </h3>
                    )}
                </div>
            </div>
        </>
    );
};

export default MoviePage;
