import '../styles/filter.css';
import { MDBBtn, MDBCard, MDBCardBody, MDBInput } from 'mdb-react-ui-kit';
import { FormSelect } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import { IGeneros, getGeneros } from '../scripts/requests';

const Filter = () => {
    const [generos, setGeneros] = useState<IGeneros>([]);

    useEffect(() => {
        const fetchGeneros = async () => {
            setGeneros(await getGeneros());
        };
    }, []);

    return (
        <MDBCard
            className="p-1 m-3 text-white filterCard"
            background="dark"
            alignment="center"
        >
            <MDBCardBody>
                <FormSelect>
                    <option value="0">Selecione o GÃªnero</option>

                    {generos.map((genero) => (
                        <option value={genero.id}>{genero.name}</option>
                    ))}
                </FormSelect>
                <MDBInput
                    label="Remember me"
                    id="form1"
                    type="text"
                    className="mt-3"
                    contrast
                />
                <div className="mt-3">
                    <MDBBtn type="submit" color="success" className="me-2">
                        Buscar
                    </MDBBtn>
                    <MDBBtn type="submit" color="light" className="ms-2">
                        Estou com sorte
                    </MDBBtn>
                </div>
            </MDBCardBody>
        </MDBCard>
    );
};

export default Filter;
