//import "../styles/content.css";
import "../styles/main.css";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from "mdb-react-ui-kit";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    IMovie,
    getMovieDetails,
    IReview,
    getMovieReviews,
} from "../scripts/requests";

const MoviePage = () => {
    const [movie, setMovie] = useState<IMovie | null>(null);
    const [reviews, setReviews] = useState<IReview[] | null>(null);
    const { movieID } = useParams();

    const isNumber = (str: string): boolean => {
        if (typeof str !== "string") {
            return false;
        }

        if (str.trim() === "") {
            return false;
        }

        return !Number.isNaN(Number(str));
    };

    useEffect(() => {
        if (movieID && isNumber(movieID)) {
            const fetchMovieDetails = async () => {
                setMovie(await getMovieDetails(parseInt(movieID)));
            };

            const fetchReviews = async () => {
                setReviews(await getMovieReviews(parseInt(movieID), "pt-BR"));
            };

            fetchMovieDetails();
            fetchReviews();
        }
    }, [movieID]);

    return (
        <>
            <div className="content-main">
                <div className="bg-image">
                    {movie && movie.backdrop_path && (
                        <img
                            className="fullscreenImage"
                            src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
                            alt="Background"
                        />
                    )}
                </div>
                <div
                    className="content"
                    style={{
                        backgroundColor: "rgba(0, 0, 0, 0.75)",
                        zIndex: 10,
                    }}
                >
                    <div className="text-white">
                        {movie && (
                            <div className="text-white m-5">
                                <MDBContainer>
                                    <MDBRow center>
                                        <MDBCol size="md-2 me-4" center>
                                            {movie.poster_path && (
                                                <img
                                                    src={
                                                        "https://image.tmdb.org/t/p/w500" +
                                                        movie.poster_path
                                                    }
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt="Pôster do filme"
                                                    style={{
                                                        maxHeight: "275px",
                                                    }}
                                                />
                                            )}
                                            {!movie.poster_path && (
                                                <img
                                                    src="/PosterNotFound.jpg"
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt="Poster do filme"
                                                    style={{
                                                        maxHeight: "275px",
                                                    }}
                                                />
                                            )}
                                        </MDBCol>
                                        <MDBCol size="md">
                                            <h1 className="text-info">
                                                {movie.title}
                                            </h1>
                                            {movie.tagline && (
                                                <h4
                                                    style={{
                                                        fontStyle: "italic",
                                                    }}
                                                >
                                                    "{movie.tagline}"
                                                </h4>
                                            )}
                                            <p>{movie.overview}</p>
                                            <p>
                                                Duração: {movie.runtime} minutos
                                            </p>
                                            <>
                                                {movie.genres.map((g) => (
                                                    /*<span
                                                        className="me-3 text-info"
                                                        style={{
                                                            fontWeight: "bold",
                                                        }}
                                                        key={g.id}
                                                    >
                                                        {g.name}
                                                    </span>*/
                                                    <MDBBtn
                                                        rounded
                                                        className="me-3"
                                                        color="info"
                                                        key={g.id}
                                                    >
                                                        {g.name}
                                                    </MDBBtn>
                                                ))}
                                            </>
                                        </MDBCol>
                                    </MDBRow>
                                </MDBContainer>
                                <MDBContainer
                                    className="mt-4"
                                    style={{ height: "auto" }}
                                >
                                    <h2 className="text-info">Reviews</h2>
                                    <>
                                        {reviews &&
                                            reviews.map((r) => (
                                                <>
                                                    <h4>{r.author}</h4>
                                                    <p key={r.id}>
                                                        {r.content}
                                                    </p>
                                                </>
                                            ))}
                                        {!reviews && (
                                            <p> Nenhuma review encontrada</p>
                                        )}
                                    </>
                                </MDBContainer>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </>
    );
};

export default MoviePage;
