import {
    MDBRow,
    MDBPagination,
    MDBPaginationItem,
    MDBPaginationLink,
} from 'mdb-react-ui-kit';
import { useActions } from '../hooks/useActions';
import { IFilter } from './content';
import { useTypedSelector } from '../hooks/useTypedSelector';

interface IFiltro {
    filterState: IFilter;
    setFilterState: React.Dispatch<React.SetStateAction<IFilter>>;
}

const CustomPagination: React.FC<IFiltro> = ({
    filterState,
    setFilterState,
}) => {
    const { searchMovies } = useActions();

    const { data } = useTypedSelector((state) => state.repositories);

    const isActive = (thisPage: number): boolean => {
        return thisPage === filterState.page;
    };

    const setPage = (newPage: number) => {
        searchMovies(filterState.genero, filterState.adulto, newPage);
    };

    const getPageNumber = (index: number): number => {
        let soma = -2 + index;

        if (filterState.page - soma <= index) {
            return index;
        }

        return filterState.page + soma;

        switch (index) {
            case 1:
                {
                    if (filterState.page - 2 <= 1) {
                        return 1;
                    }

                    return filterState.page - 2;
                }
                break;
            case 2:
                {
                    if (filterState.page - 1 <= 2) {
                        return 2;
                    }

                    return filterState.page - 1;
                }
                break;
            case 3:
                {
                    if (filterState.page <= 3) {
                        return 3;
                    }

                    return filterState.page;
                }
                break;
            case 4:
                {
                    if (filterState.page + 1 <= 4) {
                        return 4;
                    }

                    return filterState.page + 1;
                }
                break;
            default:
                {
                    if (filterState.page + 2 <= 5) {
                        return 5;
                    }

                    return filterState.page + 2;
                }
                break;
        }
    };

    const createPageNumbers = () => {
        let numberOfPages = 5;

        console.log(data.total_pages);

        if (data.total_pages < 5) {
            numberOfPages = data.total_pages;
        }

        const pageNumbers = Array.from(
            { length: numberOfPages },
            (_, index) => {
                return (
                    <MDBPaginationItem
                        active={isActive(getPageNumber(index + 1))}
                        onClick={() => setPage(getPageNumber(index + 1))}
                        key={index}
                    >
                        <MDBPaginationLink className="text-white pageNumber">
                            {getPageNumber(index + 1)}
                        </MDBPaginationLink>
                    </MDBPaginationItem>
                );
            }
        );

        return pageNumbers;
    };

    return (
        <div>
            {data.movie_details.length > 0 && (
                <MDBRow className="mt-4 mr-0">
                    <MDBPagination className="mb-0" center>
                        <MDBPaginationItem
                            onClick={() =>
                                setPage(
                                    filterState.page > 1
                                        ? filterState.page - 1
                                        : filterState.page
                                )
                            }
                        >
                            <MDBPaginationLink className="text-white pageNumber">
                                Anterior
                            </MDBPaginationLink>
                        </MDBPaginationItem>

                        {createPageNumbers()}

                        <MDBPaginationItem
                            onClick={() =>
                                setPage(
                                    filterState.page < data.total_pages
                                        ? filterState.page + 1
                                        : filterState.page
                                )
                            }
                        >
                            <MDBPaginationLink className="text-white pageNumber">
                                Próximo
                            </MDBPaginationLink>
                        </MDBPaginationItem>
                    </MDBPagination>
                </MDBRow>
            )}
        </div>
    );
};

export default CustomPagination;
