import '../styles/main.css';
import '../styles/movie-grid.css';
import {
    MDBRow,
    MDBCol,
    MDBContainer,
    MDBSpinner,
    MDBPagination,
    MDBPaginationItem,
    MDBPaginationLink,
} from 'mdb-react-ui-kit';
import Movie from './movie';
import { useTypedSelector } from '../hooks/useTypedSelector';
import Alert from 'react-bootstrap/Alert';
import { useState } from 'react';

const MovieGrid = () => {
    const [page, setPage] = useState(1);

    const { data, error, loading } = useTypedSelector(
        (state) => state.repositories
    );

    const isActive = (thisPage: number): boolean => {
        return thisPage === page;
    };

    return (
        <div>
            {error && <Alert variant="danger">{error}</Alert>}
            {loading && (
                <div className="centralizar">
                    <MDBSpinner role="status" color="dark">
                        <span className="visually-hidden">Loading...</span>
                    </MDBSpinner>
                </div>
            )}
            {!error && !loading && (
                <MDBContainer fluid>
                    <MDBRow className="col-12  g-3 m-0">
                        {data.map((movie) => (
                            <MDBCol key={movie.id}>
                                <Movie
                                    name={movie.title}
                                    imageSRC={movie.poster_path}
                                    releaseDate={movie.release_date}
                                    budget={movie.budget}
                                    revenue={movie.revenue}
                                    rating={movie.vote_average}
                                    tagline={movie.tagline}
                                />
                            </MDBCol>
                        ))}
                    </MDBRow>
                    {data.length > 0 && (
                        <MDBRow className="mt-4">
                            <MDBPagination className="mb-0" center>
                                <MDBPaginationItem>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white pageNumber"
                                    >
                                        Anterior
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(1)}
                                    onClick={() => setPage(1)}
                                >
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white pageNumber"
                                    >
                                        1
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(2)}
                                    onClick={() => setPage(2)}
                                >
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white pageNumber"
                                    >
                                        2
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(3)}
                                    onClick={() => setPage(3)}
                                >
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white pageNumber"
                                    >
                                        3
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(4)}
                                    onClick={() => setPage(4)}
                                    className="selectedPage"
                                >
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white pageNumber"
                                    >
                                        4
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(5)}
                                    onClick={() => setPage(5)}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        5
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white pageNumber"
                                    >
                                        Próximo
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                            </MDBPagination>
                        </MDBRow>
                    )}
                </MDBContainer>
            )}
        </div>
    );
};

export default MovieGrid;
