import { MDBIcon, MDBTooltip, placement } from "mdb-react-ui-kit";
import React from "react";

type TooltipProps = {
    tip: string;
    placement?: placement;
    props?: Record<string, unknown>;
    children?: React.ReactNode;
};

const Tooltip: React.FC<TooltipProps> = ({
    tip,
    placement = "right",
    props,
    children,
}) => {
    return (
        <MDBTooltip
            tag="div"
            placement={placement}
            wrapperProps={props}
            title={tip}
        >
            <>
                {children && children}
                {!children && (
                    <MDBIcon
                        color="white"
                        fas
                        icon="info-circle"
                        className="m-0"
                    />
                )}
            </>
        </MDBTooltip>
    );
};

export default Tooltip;
