import '../styles/main.css';
import '../styles/movie-grid.css';
import {
    MDBRow,
    MDBCol,
    MDBContainer,
    MDBSpinner,
    MDBPagination,
    MDBPaginationItem,
    MDBPaginationLink,
} from 'mdb-react-ui-kit';
import Movie from './movie';
import { useTypedSelector } from '../hooks/useTypedSelector';
import Alert from 'react-bootstrap/Alert';
import { IFilter } from './content';
import { useActions } from '../hooks/useActions';

import { useEffect } from 'react';

interface IFiltro {
    filterState: IFilter;
    setFilterState: React.Dispatch<React.SetStateAction<IFilter>>;
}

const MovieGrid: React.FC<IFiltro> = ({ filterState, setFilterState }) => {
    const { searchMovies } = useActions();

    const { data, error, loading } = useTypedSelector(
        (state) => state.repositories
    );

    const isActive = (thisPage: number): boolean => {
        return thisPage === filterState.page;
    };

    const setPage = (newPage: number) => {
        searchMovies(filterState.genero, filterState.adulto, newPage);
    };

    const teste = () => {
        setFilterState({
            genero: filterState.genero,
            adulto: filterState.adulto,
            page: data.page,
            total_pages: data.total_pages,
            total_results: data.total_results,
        });

        console.log(data.total_results);
    };

    useEffect(() => {
        const fetchGeneros = async () => {
            setGeneros(await getGeneros());
        };

        fetchGeneros();
    }, []);

    const page1 = (): number => {
        if (filterState.page - 2 <= 1) {
            return 1;
        }

        return filterState.page - 2;
    };
    const page2 = (): number => {
        if (filterState.page - 1 <= 2) {
            return 2;
        }

        return filterState.page - 1;
    };
    const page3 = (): number => {
        if (filterState.page <= 3) {
            return 3;
        }

        return filterState.page;
    };
    const page4 = (): number => {
        if (filterState.page + 1 <= 4) {
            return 4;
        }

        return filterState.page + 1;
    };
    const page5 = (): number => {
        if (filterState.page + 2 <= 5) {
            return 5;
        }

        return filterState.page + 2;
    };

    return (
        <div>
            {error && <Alert variant="danger">{error}</Alert>}
            {loading && (
                <div className="centralizar">
                    <MDBSpinner role="status" color="dark">
                        <span className="visually-hidden">Loading...</span>
                    </MDBSpinner>
                </div>
            )}
            {!error && !loading && (
                <MDBContainer fluid>
                    {data.movie_details.length > 0 && (
                        <MDBRow className="mt-4 mr-0">
                            <MDBPagination className="mb-0" center>
                                <MDBPaginationItem
                                    onClick={() =>
                                        setPage(
                                            filterState.page > 1
                                                ? filterState.page - 1
                                                : filterState.page
                                        )
                                    }
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        Anterior
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page1())}
                                    onClick={() => setPage(page1())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page1()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page2())}
                                    onClick={() => setPage(page2())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page2()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page3())}
                                    onClick={() => setPage(page3())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page3()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page4())}
                                    onClick={() => setPage(page4())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page4()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page5())}
                                    onClick={() => setPage(page5())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page5()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    onClick={() =>
                                        setPage(filterState.page + 1)
                                    }
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        Próximo
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                            </MDBPagination>
                        </MDBRow>
                    )}
                    <MDBRow className="col-12  g-3 m-0">
                        {data.movie_details.map((movie) => (
                            <MDBCol key={movie.id}>
                                <Movie
                                    name={movie.title}
                                    imageSRC={movie.poster_path}
                                    releaseDate={movie.release_date}
                                    budget={movie.budget}
                                    revenue={movie.revenue}
                                    rating={movie.vote_average}
                                    tagline={movie.tagline}
                                />
                            </MDBCol>
                        ))}
                    </MDBRow>
                    {data.movie_details.length > 0 && (
                        <MDBRow className="mt-4 mr-0">
                            <MDBPagination className="mb-0" center>
                                <MDBPaginationItem
                                    onClick={() =>
                                        setPage(
                                            filterState.page > 1
                                                ? filterState.page - 1
                                                : filterState.page
                                        )
                                    }
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        Anterior
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page1())}
                                    onClick={() => setPage(page1())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page1()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page2())}
                                    onClick={() => setPage(page2())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page2()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page3())}
                                    onClick={() => setPage(page3())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page3()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page4())}
                                    onClick={() => setPage(page4())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page4()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    active={isActive(page5())}
                                    onClick={() => setPage(page5())}
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        {page5()}
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem
                                    onClick={() =>
                                        setPage(filterState.page + 1)
                                    }
                                >
                                    <MDBPaginationLink className="text-white pageNumber">
                                        Próximo
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                            </MDBPagination>
                        </MDBRow>
                    )}
                </MDBContainer>
            )}
        </div>
    );
};

export default MovieGrid;
