import '../styles/main.css';
import {
    MDBRow,
    MDBCol,
    MDBContainer,
    MDBSpinner,
    MDBPagination,
    MDBPaginationItem,
    MDBPaginationLink,
} from 'mdb-react-ui-kit';
import Movie from './movie';
import { useTypedSelector } from '../hooks/useTypedSelector';
import Alert from 'react-bootstrap/Alert';

const MovieGrid = () => {
    var page = 3;

    const { data, error, loading } = useTypedSelector(
        (state) => state.repositories
    );

    const isActive = (thisPage: number): boolean => {
        return thisPage === page;
    };

    return (
        <div>
            {error && <Alert variant="danger">{error}</Alert>}
            {loading && (
                <div className="centralizar">
                    <MDBSpinner role="status" color="dark">
                        <span className="visually-hidden">Loading...</span>
                    </MDBSpinner>
                </div>
            )}
            {!error && !loading && (
                <MDBContainer fluid>
                    <MDBRow className="col-12  g-3 m-0">
                        {data.map((movie) => (
                            <MDBCol key={movie.id}>
                                <Movie
                                    name={movie.title}
                                    imageSRC={movie.poster_path}
                                    releaseDate={movie.release_date}
                                    budget={movie.budget}
                                    revenue={movie.revenue}
                                    rating={movie.vote_average}
                                    tagline={movie.tagline}
                                />
                            </MDBCol>
                        ))}
                    </MDBRow>
                    {data.length > 0 && (
                        <MDBRow className="mt-4">
                            <MDBPagination className="mb-0" center>
                                <MDBPaginationItem>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white"
                                    >
                                        Anterior
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem active={isActive(1)}>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white"
                                    >
                                        1
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem active={isActive(2)}>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white"
                                    >
                                        2
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem active={isActive(3)}>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white"
                                    >
                                        3
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem active={isActive(7)}>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white"
                                    >
                                        4
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem active={isActive(5)}>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white"
                                    >
                                        5
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                                <MDBPaginationItem>
                                    <MDBPaginationLink
                                        href="#"
                                        className="text-white"
                                    >
                                        Próximo
                                    </MDBPaginationLink>
                                </MDBPaginationItem>
                            </MDBPagination>
                        </MDBRow>
                    )}
                </MDBContainer>
            )}
        </div>
    );
};

export default MovieGrid;
