const axios = require('axios');

interface IGenero {
    id: number;
    name: string;
}

export interface IGeneros extends Array<IGenero> {}

export const getGeneros = async (): Promise<IGeneros> => {
    const generos: IGeneros = [];

    await axios
        .get('http://localhost:3001/generos')
        .then((response: any) => {
            response.data.genres.forEach((g: IGenero) => {
                generos.push(g);
            });
        })
        .catch((error: any) => {
            console.log(error);
        });

    const customSort = (a: IGenero, b: IGenero) => {
        if (a.name < b.name) {
            return -1;
        }
        if (a.name > b.name) {
            return 1;
        }
        return 0;
    };

    generos.sort(customSort);

    return generos;
};

interface IMovie {
    poster_path: string | undefined;
    adult: boolean | undefined;
    overview: string | undefined;
    release_date: string | undefined;
    genres: Array<number> | undefined;
    id: number | undefined;
    original_title: string | undefined;
    title: string | undefined;
    backdrop_path: string | undefined;
    vote_average: number | undefined;
    budget: number | undefined;
    revenue: number | undefined;
    runtime: number | undefined;
    tagline: string | undefined;
}

export interface IMovies extends Array<IMovie> {}

export const getMovies = async (): Promise<IGeneros> => {
    const movies: IGeneros = [];

    await axios
        .get('http://localhost:3001/descobrir')
        .then((response: any) => {
            if (response.data) {
                response.data.array.forEach((m: IMovie) => {
                    movies.push(m);
                });
            }
        })
        .catch((error: any) => {
            console.log(error);
        });

    return movies;
};
