import '../styles/filter.css';
import '../styles/main.css';
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBSwitch,
} from 'mdb-react-ui-kit';
import { FormSelect } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import { IGeneros, getGeneros } from '../scripts/requests';
import { useActions } from '../hooks/useActions';

const Filter = () => {
    const [generos, setGeneros] = useState<IGeneros>([]);
    const [adulto, setAdulto] = useState(false);
    const [genero, setGenero] = useState(0);
    const { searchMovies } = useActions();

    useEffect(() => {
        const fetchGeneros = async () => {
            setGeneros(await getGeneros());
        };

        fetchGeneros();
    }, []);

    const buscarClick = async () => {
        searchMovies(adulto, 1);
    };

    return (
        <MDBCard className="p-1 m-3 text-white filterCard" background="dark">
            <MDBCardBody>
                <FormSelect onChange={(e) => setGenero(e.target.key)}>
                    <option key="0" value="0">
                        Selecione o Gênero
                    </option>

                    {generos.map((genero) => (
                        <option key={genero.id} value={genero.id}>
                            {genero.name}
                        </option>
                    ))}
                </FormSelect>
                <MDBInput
                    label="Remember me"
                    id="form1"
                    type="text"
                    className="mt-3"
                    contrast
                />
                <div className="mt-3">
                    <MDBSwitch
                        id="adult"
                        className="me-3"
                        label="Mostrar filmes adultos (conteúdo sexual)"
                        onChange={() => setAdulto(!adulto)}
                        checked={adulto}
                    />
                </div>
                <div className="centralizar mt-3">
                    <MDBBtn
                        type="submit"
                        color="success"
                        className="me-2"
                        onClick={buscarClick}
                    >
                        Buscar
                    </MDBBtn>
                    <MDBBtn type="submit" color="light" className="ms-2">
                        Estou com sorte
                    </MDBBtn>
                </div>
            </MDBCardBody>
        </MDBCard>
    );
};

export default Filter;
