import "../styles/main.css";
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBBtnGroup,
    MDBBtn,
    MDBAccordion,
    MDBAccordionItem,
    MDBIcon,
    MDBRadio,
} from "mdb-react-ui-kit";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    IMovie,
    getMovieDetails,
    IReview,
    getMovieReviews,
} from "../scripts/requests";

const MoviePage = () => {
    const [movie, setMovie] = useState<IMovie | null>(null);
    const [reviews, setReviews] = useState<IReview[] | null>(null);
    const { movieID } = useParams();

    const isNumber = (str: string): boolean => {
        if (typeof str !== "string") {
            return false;
        }

        if (str.trim() === "") {
            return false;
        }

        return !Number.isNaN(Number(str));
    };

    const fetchReviews = async (language: string) => {
        if (movieID && isNumber(movieID)) {
            setReviews(await getMovieReviews(parseInt(movieID), language));
        }
    };

    useEffect(() => {
        if (movieID && isNumber(movieID)) {
            const fetchMovieDetails = async () => {
                setMovie(await getMovieDetails(parseInt(movieID)));
            };

            fetchMovieDetails();
            fetchReviews("pt-BR");
        }
    }, [movieID]);

    useEffect(() => {
        // Garante que vai mostrar a parte de cima da pÃ¡gina
        //window.scrollTo(0, 0);
    }, []);

    const languageClick = (language: string) => {
        /*const enButton = document.getElementById("enButton");
        const ptButton = document.getElementById("ptButton");

        if (enButton && ptButton) {
            console.log(language);

            if (language === "pt-BR") {
            ptButton.classList.toggle("active");
            enButton.classList.toggle("active");
            } else {
                enButton.classList.add("active");
                ptButton.classList.remove("active");
            }
        }*/

        fetchReviews(language);
    };

    const padTo2Digits = (num: number) => {
        return num.toString().padStart(2, "0");
    };

    const formatDate = (date: Date) => {
        let d = new Date(date);

        return [
            padTo2Digits(d.getDate()),
            padTo2Digits(d.getMonth() + 1),
            d.getFullYear(),
        ].join("/");
    };

    return (
        <>
            <div className="content-main">
                <div className="bg-image">
                    {movie && movie.backdrop_path && (
                        <img
                            className="fullscreenImage"
                            src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
                            alt="Background"
                        />
                    )}
                </div>
                <div
                    className="content"
                    style={{
                        backgroundColor: "rgba(0, 0, 0, 0.75)",
                        zIndex: 10,
                    }}
                >
                    <div className="text-white">
                        {movie && (
                            <div className="text-white m-5">
                                <MDBContainer>
                                    <MDBRow center>
                                        <MDBCol size="md-2 me-4 centralizar">
                                            {movie.poster_path && (
                                                <img
                                                    src={
                                                        "https://image.tmdb.org/t/p/w500" +
                                                        movie.poster_path
                                                    }
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt="PÃ´ster do filme"
                                                    style={{
                                                        maxHeight: "275px",
                                                    }}
                                                />
                                            )}
                                            {!movie.poster_path && (
                                                <img
                                                    src="/PosterNotFound.jpg"
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt="Poster do filme"
                                                    style={{
                                                        maxHeight: "275px",
                                                    }}
                                                />
                                            )}
                                        </MDBCol>
                                        <MDBCol size="md">
                                            <h1 className="text-info">
                                                {movie.title}
                                            </h1>
                                            {movie.tagline && (
                                                <h4
                                                    style={{
                                                        fontStyle: "italic",
                                                    }}
                                                >
                                                    "{movie.tagline}"
                                                </h4>
                                            )}
                                            <p>{movie.overview}</p>
                                            <p>
                                                <MDBIcon
                                                    far
                                                    icon="clock"
                                                    className="me-2"
                                                />
                                                {movie.runtime} minutos
                                            </p>
                                            <>
                                                {movie.genres.map((g) => (
                                                    <MDBBtn
                                                        rounded
                                                        className="m-1 transparentBG active"
                                                        color="info"
                                                        key={g.id}
                                                    >
                                                        {g.name}
                                                    </MDBBtn>
                                                ))}
                                            </>
                                        </MDBCol>
                                    </MDBRow>
                                </MDBContainer>
                                <MDBContainer
                                    className="mt-4"
                                    style={{ height: "auto" }}
                                >
                                    <h2 className="text-info">Reviews</h2>
                                    <MDBBtnGroup className="mb-3">
                                        <MDBRadio
                                            id="ptButton"
                                            name="language"
                                            className=""
                                            wrapperClass="me-2 transparentBG"
                                            onClick={() =>
                                                languageClick("pt-BR")
                                            }
                                            btn
                                            wrapperTag="span"
                                            label="PortuguÃªs"
                                            defaultChecked
                                        />
                                        <MDBRadio
                                            id="enButton"
                                            name="language"
                                            onClick={() =>
                                                languageClick("en-US")
                                            }
                                            wrapperTag="span"
                                            btn
                                            label="InglÃªs"
                                        />
                                    </MDBBtnGroup>
                                    <>
                                        {reviews && (
                                            <MDBAccordion flush>
                                                {reviews.map((r, index) => (
                                                    <MDBAccordionItem
                                                        key={index}
                                                        collapseId={index + 1}
                                                        className="text-white"
                                                        headerTitle={
                                                            <>
                                                                <div className="text-warning">
                                                                    <MDBIcon
                                                                        icon="star"
                                                                        className="mb-2 me-2"
                                                                    />
                                                                    {
                                                                        r
                                                                            .author_details
                                                                            .rating
                                                                    }
                                                                </div>
                                                                <span>
                                                                    `${r.author}{" "}
                                                                    ($
                                                                    {formatDate(
                                                                        r.created_at
                                                                    )}
                                                                    )`
                                                                </span>
                                                            </>
                                                        }
                                                        style={{
                                                            backgroundColor:
                                                                "rgba(0, 0, 0, 0.5)",
                                                        }}
                                                    >
                                                        {r.content}
                                                    </MDBAccordionItem>
                                                ))}
                                            </MDBAccordion>
                                        )}
                                        {!reviews && (
                                            <p> Nenhuma review encontrada</p>
                                        )}
                                    </>
                                </MDBContainer>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </>
    );
};

export default MoviePage;
