import "../styles/main.css";
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBBtnGroup,
    MDBBtn,
    MDBAccordion,
    MDBAccordionItem,
    MDBIcon,
    MDBRadio,
    MDBCard,
    MDBCardImage,
    MDBCardBody,
    MDBCardText,
    MDBCollapse,
} from "mdb-react-ui-kit";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    IMovie,
    getMovieDetails,
    IReview,
    getMovieReviews,
    ICastMember,
    getCastMembers,
    IWatchProviders,
    getWatchProviders,
} from "../scripts/requests";

const MoviePage = () => {
    const [movie, setMovie] = useState<IMovie | null>(null);
    const [reviews, setReviews] = useState<IReview[] | null>(null);
    const [castMembers, setCastMembers] = useState<ICastMember[] | null>(null);
    const [watchProviders, setWatchProviders] =
        useState<IWatchProviders | null>(null);
    const [showShow, setShowShow] = useState(false);
    const { movieID } = useParams();

    const toggleShow = () => setShowShow(!showShow);

    const isNumber = (str: string): boolean => {
        if (typeof str !== "string") {
            return false;
        }

        if (str.trim() === "") {
            return false;
        }

        return !Number.isNaN(Number(str));
    };

    const fetchReviews = async (language: string) => {
        if (movieID && isNumber(movieID)) {
            setReviews(await getMovieReviews(parseInt(movieID), language));
        }
    };

    useEffect(() => {
        if (movieID && isNumber(movieID)) {
            const fetchMovieDetails = async () => {
                setMovie(await getMovieDetails(parseInt(movieID)));
            };

            const fetchMovieCast = async () => {
                setCastMembers(await getCastMembers(parseInt(movieID)));
            };

            const fetchWatchProviders = async () => {
                setWatchProviders(await getWatchProviders(parseInt(movieID)));
            };

            fetchMovieDetails();
            fetchReviews("pt-BR");
            fetchMovieCast();
            fetchWatchProviders();
        }
    }, [movieID]);

    useEffect(() => {
        // Garante que vai mostrar a parte de cima da página
        window.scrollTo(0, 0);
    }, []);

    const languageClick = (language: string) => {
        fetchReviews(language);
    };

    const padTo2Digits = (num: number) => {
        return num.toString().padStart(2, "0");
    };

    const formatDate = (date: Date) => {
        let d = new Date(date);

        return [
            padTo2Digits(d.getDate()),
            padTo2Digits(d.getMonth() + 1),
            d.getFullYear(),
        ].join("/");
    };

    const formatDateStr = (date: string) => {
        let datePart: RegExpMatchArray | null = date.match(/\d+/g);
        let year = "";
        let month = "";
        let day = "";

        if (datePart) {
            year = datePart[0];
            month = datePart[1];
            day = datePart[2];
        }

        return day + "/" + month + "/" + year;
    };

    const priceSplitter = (number: number) =>
        number && number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");

    return (
        <>
            <div className="content-main">
                <div className="bg-image">
                    {movie && movie.backdrop_path && (
                        <img
                            className="fullscreenImage"
                            src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
                            alt="Background"
                        />
                    )}
                </div>
                <div
                    className="content"
                    style={{
                        backgroundColor: "rgba(0, 0, 0, 0.8)",
                        zIndex: 10,
                    }}
                >
                    <div className="text-white">
                        {movie && (
                            <div className="text-white m-5">
                                <MDBContainer>
                                    <MDBRow center>
                                        <MDBCol size="md-2 me-4 centralizar">
                                            {movie.poster_path && (
                                                <img
                                                    src={
                                                        "https://image.tmdb.org/t/p/w500" +
                                                        movie.poster_path
                                                    }
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt="Pôster do filme"
                                                    style={{
                                                        maxHeight: "275px",
                                                    }}
                                                />
                                            )}
                                            {!movie.poster_path && (
                                                <img
                                                    src="/PosterNotFound.jpg"
                                                    className="img-fluid rounded shadow-4 m-3"
                                                    alt="Poster do filme"
                                                    style={{
                                                        maxHeight: "275px",
                                                    }}
                                                />
                                            )}
                                        </MDBCol>
                                        <MDBCol size="md">
                                            <h1 className="text-info">
                                                <span>{movie.title}</span>
                                            </h1>
                                            {movie.tagline && (
                                                <h4
                                                    style={{
                                                        fontStyle: "italic",
                                                    }}
                                                >
                                                    "{movie.tagline}"
                                                </h4>
                                            )}
                                            <p>{movie.overview}</p>
                                            <p>
                                                <span className="text-warning me-3">
                                                    <MDBIcon
                                                        far
                                                        icon="star"
                                                        className="me-2"
                                                    />
                                                    {movie.vote_average.toFixed(
                                                        1
                                                    )}
                                                </span>
                                                <span className="me-3">
                                                    <MDBIcon
                                                        fas
                                                        icon="calendar-alt"
                                                        className="me-2"
                                                    />
                                                    {formatDateStr(
                                                        movie.release_date
                                                    )}
                                                </span>
                                                <span>
                                                    <MDBIcon
                                                        far
                                                        icon="clock"
                                                        className="me-2"
                                                    />
                                                    {movie.runtime} minutos
                                                </span>
                                            </p>
                                            <p>
                                                <span className="me-3">
                                                    <span className="text-white me-2">
                                                        Orçamento:
                                                    </span>
                                                    <span
                                                        className="text-success"
                                                        style={{
                                                            fontWeight: "bold",
                                                        }}
                                                    >
                                                        <MDBIcon
                                                            fas
                                                            icon="dollar-sign"
                                                            className="me-2"
                                                        />
                                                        {movie.budget > 0 &&
                                                            priceSplitter(
                                                                movie.budget
                                                            )}
                                                        {movie.budget <= 0 &&
                                                            "-"}
                                                    </span>
                                                </span>
                                                <span>
                                                    <span className="text-white me-2">
                                                        Receita:
                                                    </span>
                                                    <span
                                                        className="text-success"
                                                        style={{
                                                            fontWeight: "bold",
                                                        }}
                                                    >
                                                        <MDBIcon
                                                            fas
                                                            icon="dollar-sign"
                                                            className="me-2"
                                                        />
                                                        {movie.revenue > 0 &&
                                                            priceSplitter(
                                                                movie.revenue
                                                            )}
                                                        {movie.revenue <= 0 &&
                                                            "-"}
                                                    </span>
                                                </span>
                                            </p>
                                            <>
                                                {movie.genres.map((g) => (
                                                    <MDBBtn
                                                        rounded
                                                        className="m-1 transparentBG active"
                                                        color="info"
                                                        key={g.id}
                                                    >
                                                        {g.name}
                                                    </MDBBtn>
                                                ))}
                                            </>
                                        </MDBCol>
                                    </MDBRow>
                                </MDBContainer>
                                {watchProviders && (
                                    <MDBContainer className="mt-3">
                                        <h2 className="text-info">
                                            Provedores
                                        </h2>
                                        {watchProviders.isAvailable && (
                                            <>
                                                {watchProviders.flatrate.map(
                                                    (w) => (
                                                        <img
                                                            src={`https://image.tmdb.org/t/p/original${w.logo_path}`}
                                                            alt={
                                                                w.provider_name
                                                            }
                                                            style={{
                                                                maxWidth:
                                                                    "50px",
                                                            }}
                                                        ></img>
                                                    )
                                                )}
                                                {watchProviders.buy.map((w) => (
                                                    <img
                                                        src={`https://image.tmdb.org/t/p/original${w.logo_path}`}
                                                        alt={w.provider_name}
                                                        style={{
                                                            maxWidth: "50px",
                                                        }}
                                                    ></img>
                                                ))}
                                                {watchProviders.rent.map(
                                                    (w) => (
                                                        <img
                                                            src={`https://image.tmdb.org/t/p/original${w.logo_path}`}
                                                            alt={
                                                                w.provider_name
                                                            }
                                                            style={{
                                                                maxWidth:
                                                                    "50px",
                                                            }}
                                                        ></img>
                                                    )
                                                )}
                                            </>
                                        )}

                                        {!watchProviders.isAvailable && (
                                            <p>
                                                Filme ainda não disponível por
                                                streaming no Brasil
                                            </p>
                                        )}
                                    </MDBContainer>
                                )}
                                {castMembers && castMembers.length > 0 && (
                                    <MDBContainer className="mt-3">
                                        <h2 className="text-info">Atores</h2>
                                        <MDBRow>
                                            {castMembers &&
                                                castMembers.map((c, index) => (
                                                    <>
                                                        {index < 6 && (
                                                            <MDBCol
                                                                sm="2"
                                                                className="p-1 text-dark"
                                                            >
                                                                <MDBCard alignment="center">
                                                                    {c.profile_path && (
                                                                        <MDBCardImage
                                                                            src={`https://image.tmdb.org/t/p/w500${c.profile_path}`}
                                                                            alt="elenco"
                                                                            position="top"
                                                                        />
                                                                    )}
                                                                    <MDBCardBody className="p-1">
                                                                        <MDBCardText>
                                                                            <small>
                                                                                <p className="text-white m-0">
                                                                                    {
                                                                                        c.name
                                                                                    }
                                                                                </p>
                                                                                <p className="text-info m-0">
                                                                                    {
                                                                                        c.character
                                                                                    }
                                                                                </p>
                                                                            </small>
                                                                        </MDBCardText>
                                                                    </MDBCardBody>
                                                                </MDBCard>
                                                            </MDBCol>
                                                        )}
                                                    </>
                                                ))}
                                        </MDBRow>

                                        {castMembers.length > 6 && (
                                            <>
                                                <span
                                                    className="fake-link"
                                                    onClick={toggleShow}
                                                >
                                                    {showShow &&
                                                        "Mostrar menos"}
                                                    {!showShow &&
                                                        "Mostrar mais"}
                                                </span>
                                                <MDBCollapse show={showShow}>
                                                    <MDBRow>
                                                        {castMembers &&
                                                            castMembers.map(
                                                                (c, index) => (
                                                                    <>
                                                                        {index >=
                                                                            6 && (
                                                                            <MDBCol
                                                                                sm="2"
                                                                                className="p-1 text-dark"
                                                                            >
                                                                                <MDBCard alignment="center">
                                                                                    {c.profile_path && (
                                                                                        <MDBCardImage
                                                                                            src={`https://image.tmdb.org/t/p/w500${c.profile_path}`}
                                                                                            alt="elenco"
                                                                                            position="top"
                                                                                        />
                                                                                    )}
                                                                                    <MDBCardBody className="p-1">
                                                                                        <MDBCardText>
                                                                                            <small>
                                                                                                <p className="text-white m-0">
                                                                                                    {
                                                                                                        c.name
                                                                                                    }
                                                                                                </p>
                                                                                                <p className="text-info m-0">
                                                                                                    {
                                                                                                        c.character
                                                                                                    }
                                                                                                </p>
                                                                                            </small>
                                                                                        </MDBCardText>
                                                                                    </MDBCardBody>
                                                                                </MDBCard>
                                                                            </MDBCol>
                                                                        )}
                                                                    </>
                                                                )
                                                            )}
                                                    </MDBRow>
                                                </MDBCollapse>
                                            </>
                                        )}
                                    </MDBContainer>
                                )}
                                <MDBContainer
                                    className="mt-4"
                                    style={{ height: "auto" }}
                                >
                                    <h2 className="text-info">Reviews</h2>
                                    <MDBBtnGroup className="mb-3">
                                        <MDBRadio
                                            id="ptButton"
                                            name="language"
                                            className=""
                                            wrapperClass="me-2 transparentBG"
                                            onClick={() =>
                                                languageClick("pt-BR")
                                            }
                                            btn
                                            wrapperTag="span"
                                            label="Português"
                                            defaultChecked
                                        />
                                        <MDBRadio
                                            id="enButton"
                                            name="language"
                                            onClick={() =>
                                                languageClick("en-US")
                                            }
                                            wrapperTag="span"
                                            btn
                                            label="Inglês"
                                        />
                                    </MDBBtnGroup>
                                    <>
                                        {reviews && (
                                            <MDBAccordion flush>
                                                {reviews.map((r, index) => (
                                                    <MDBAccordionItem
                                                        key={index}
                                                        collapseId={index + 1}
                                                        className="text-white"
                                                        headerTitle={
                                                            <>
                                                                {r
                                                                    .author_details
                                                                    .rating && (
                                                                    <div className="text-warning me-2">
                                                                        <MDBIcon
                                                                            icon="star"
                                                                            className="me-2"
                                                                        />
                                                                        {
                                                                            r
                                                                                .author_details
                                                                                .rating
                                                                        }
                                                                    </div>
                                                                )}
                                                                <span>
                                                                    {r.author} (
                                                                    {formatDate(
                                                                        r.created_at
                                                                    )}
                                                                    )
                                                                </span>
                                                            </>
                                                        }
                                                        style={{
                                                            backgroundColor:
                                                                "rgba(0, 0, 0, 0.5)",
                                                        }}
                                                    >
                                                        {r.content}
                                                    </MDBAccordionItem>
                                                ))}
                                            </MDBAccordion>
                                        )}
                                        {!reviews && (
                                            <p> Nenhuma review encontrada</p>
                                        )}
                                    </>
                                </MDBContainer>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </>
    );
};

export default MoviePage;
