import '../styles/filter.css';
import '../styles/main.css';
import { MDBBtn, MDBCard, MDBCardBody, MDBSwitch } from 'mdb-react-ui-kit';
import { useEffect, useState } from 'react';
import { IGeneros, getGeneros, getMoviesByKeyword } from '../scripts/requests';
import { useActions } from '../hooks/useActions';
import { IFilter } from './content';
import Select, { MultiValue, SingleValue } from 'react-select';
import AsyncSelect from 'react-select/async';

interface IFiltro {
    filterState: IFilter;
    setFilterState: React.Dispatch<React.SetStateAction<IFilter>>;
}

interface ISelectOption {
    value: number;
    label: string;
}

const Filter: React.FC<IFiltro> = ({ filterState, setFilterState }) => {
    const [generos, setGeneros] = useState<IGeneros>([]);
    const [filmesPesquisa, setFilmesPesquisa] = useState<
        {
            id: number;
            title: string;
        }[]
    >([]);
    const [adulto, setAdulto] = useState(false);
    const [genero, setGenero] = useState('');
    const [sort, setSort] = useState('4');
    const [keyword, setKeyword] = useState('');
    const { searchMovies } = useActions();

    useEffect(() => {
        const fetchGeneros = async () => {
            setGeneros(await getGeneros());
        };

        fetchGeneros();
    }, []);

    const buscarClick = async () => {
        searchMovies(genero, adulto, 1, sort);

        setFilterState({
            genero: genero,
            adulto: adulto,
            sort: sort,
        });
    };

    const onGeneroChange = (values: MultiValue<ISelectOption>) => {
        var generosText = '';

        values.forEach((e) => {
            if (generosText !== '') {
                generosText += ',';
            }

            generosText += e.value;
        });

        setGenero(generosText);
        console.log(generosText);
    };

    const onSortChange = (
        value: SingleValue<{ value: string; label: string }>
    ) => {
        if (value) {
            setSort(value.value);
        }
    };

    const getGeneroOptions = () => {
        var generosOptions: ISelectOption[] = [];

        generos.map((genero) =>
            generosOptions.push({ value: genero.id, label: genero.name })
        );

        return generosOptions;
    };

    const sortOptions = [
        { value: '0', label: 'Avaliação (maiores primeiro)' },
        { value: '1', label: 'Avaliação (menores primeiro)' },
        {
            value: '2',
            label: 'Data de lançamento (mais recentes primeiro)',
        },
        { value: '3', label: 'Data de lançamento (mais antigos primeiro)' },
        { value: '4', label: 'Popularidade atual' },
        { value: '5', label: 'Popularidade histórica' },
        { value: '6', label: 'Receita gerada (maiores primeiro)' },
        { value: '7', label: 'Receita gerada (menores primeiro)' },
    ];

    const filterColors = async (keyword: string) => {
        return await getMoviesByKeyword(
            keyword
        ); /*[{ value: 0, label: 'batman' }].filter((i) =>
            i.label.toLowerCase().includes(keyword.toLowerCase())
        );*/
    };

    const loadOptions = (keyword: string, callback: (options: any) => void) => {
        setTimeout(() => {
            callback(filterColors(keyword));
        }, 1000);
    };

    const promiseOptions = (inputValue: string) =>
        new Promise<any[]>((resolve) => {
            setTimeout(() => {
                resolve(filterColors(inputValue));
            }, 1000);
        });

    const getMovieByName = (newKeyword: string) => {
        setKeyword(newKeyword);
    };

    return (
        <div>
            <MDBCard className="p-1 m-3 filterCard" background="dark">
                <MDBCardBody>
                    <AsyncSelect
                        placeholder={'Pesquisar...'}
                        noOptionsMessage={() => 'Nada encontrado'}
                        cacheOptions
                        loadOptions={promiseOptions}
                        defaultOptions
                        /*onInputChange={getMovieByName}*/
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 11,
                            }),
                        }}
                    />
                    <Select
                        onChange={(values) => onGeneroChange(values)}
                        className="text-black mt-2"
                        placeholder={'Gênero'}
                        noOptionsMessage={() => 'Nada encontrado'}
                        isMulti
                        options={getGeneroOptions()}
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 10,
                            }),
                        }}
                    />
                    <Select
                        onChange={(value) => onSortChange(value)}
                        className="text-black mt-2"
                        placeholder={'Ordenação'}
                        noOptionsMessage={() => 'Nada encontrado'}
                        defaultValue={[sortOptions[4]]}
                        options={sortOptions}
                        styles={{
                            container: (base) => ({
                                ...base,
                                zIndex: 9,
                            }),
                        }}
                    />
                    {/*<FormSelect onChange={(e) => setGenero(e.target.value)}>
                        <option key="0" value="0">
                            Todos os gêneros
                        </option>

                        {generos.map((genero) => (
                            <option key={genero.id} value={genero.id}>
                                {genero.name}
                            </option>
                        ))}
                    </FormSelect>
                    <FormSelect
                        className="mt-2"
                        value={sort}
                        onChange={(e) => setSort(e.target.value)}
                    >
                        <option key="0" value="0">
                            Avaliação (maiores primeiro)
                        </option>
                        <option key="1" value="1">
                            Avaliação (menores primeiro)
                        </option>
                        <option key="2" value="2">
                            Data de lançamento (mais recentes primeiro)
                        </option>
                        <option key="3" value="3">
                            Data de lançamento (mais antigos primeiro)
                        </option>
                        <option key="4" value="4">
                            Popularidade atual
                        </option>
                        <option key="5" value="5">
                            Popularidade histórica
                        </option>
                        <option key="6" value="6">
                            Receita gerada (maiores primeiro)
                        </option>
                        <option key="7" value="7">
                            Receita gerada (menores primeiro)
                        </option>
                    </FormSelect>*/}
                    <div className="mt-3">
                        <MDBSwitch
                            id="adult"
                            className="me-3"
                            label="Mostrar filmes adultos (conteúdo sexual)"
                            onChange={() => setAdulto(!adulto)}
                            checked={adulto}
                        />
                    </div>
                    <div className="centralizar mt-3">
                        <MDBBtn
                            type="submit"
                            color="success"
                            className="me-2"
                            onClick={buscarClick}
                        >
                            Buscar
                        </MDBBtn>
                        <MDBBtn type="submit" color="light" className="ms-2">
                            Estou com sorte
                        </MDBBtn>
                    </div>
                </MDBCardBody>
            </MDBCard>
        </div>
    );
};

export default Filter;
