import { MultiValue, PropsValue } from "react-select";
import AsyncSelect from "react-select/async";
import { ISelectOption } from "../../../utils/interfaces";

interface CustomAsyncSelectProps {
    placeholder: string;
    value: PropsValue<ISelectOption>;
    loadOptions: (keyword: string) => Promise<any[]>;
    zIndex: number;
    onChange: (value: MultiValue<ISelectOption>) => void;
}

const CustomAsyncSelect = ({
    placeholder,
    value,
    loadOptions,
    zIndex,
    onChange,
}: CustomAsyncSelectProps) => {
    return (
        <AsyncSelect
            placeholder={placeholder}
            className="mt-1 mb-1"
            noOptionsMessage={() => "Nada encontrado"}
            onChange={onChange}
            cacheOptions
            loadOptions={loadOptions}
            defaultOptions
            loadingMessage={() => "Buscando..."}
            isMulti
            value={value}
            styles={{
                container: (base) => ({
                    ...base,
                    zIndex: zIndex,
                }),
                valueContainer: (base) => ({
                    ...base,
                    padding: "3px",
                }),
                menu: (base) => ({
                    ...base,
                    color: "white",
                    backgroundColor: "black",
                    border: "2px solid rgb(57, 192, 237)",
                }),
                noOptionsMessage: (base) => ({
                    ...base,
                    color: "white",
                }),
                placeholder: (base) => ({
                    ...base,
                    color: "white",
                }),
                option: (base) => ({
                    ...base,
                    ":hover": {
                        backgroundColor: "rgba(57, 192, 237, 0.5)",
                    },
                }),
                multiValue: (base) => ({
                    ...base,
                    backgroundColor: "rgba(57, 192, 237, 0.8)",
                }),
                multiValueLabel: (base) => ({
                    ...base,
                    color: "white",
                }),
                multiValueRemove: (base) => ({
                    ...base,
                    ":hover": {
                        backgroundColor: "white",
                    },
                }),
            }}
        />
    );
};

export default CustomAsyncSelect;
