import '../styles/movie.css';
import {
    MDBCard,
    MDBCardTitle,
    MDBCardText,
    MDBCardBody,
    MDBCardImage,
    MDBRow,
    MDBCol,
    MDBIcon,
    MDBCardFooter,
} from 'mdb-react-ui-kit';

interface MovieProps {
    name: string;
    imageSRC: string;
    releaseDate: string;
    budget: number;
    revenue: number;
    rating: number;
    tagline: string;
}

const priceSplitter = (number: number) =>
    number && number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.');

const Movie: React.FC<MovieProps> = ({
    name,
    imageSRC,
    releaseDate,
    budget,
    revenue,
    rating,
    tagline,
}) => {
    function formatDate(date: string) {
        let datePart: RegExpMatchArray | null = date.match(/\d+/g);
        let year = '';
        let month = '';
        let day = '';

        if (datePart) {
            year = datePart[0];
            month = datePart[1];
            day = datePart[2];
        }

        return day + '/' + month + '/' + year;
    }

    return (
        <MDBCard
            background="dark"
            className="text-white movieCard"
            alignment="center"
        >
            <MDBRow className="g-0 m-0">
                <MDBCol md="6">
                    <MDBCardImage
                        src={'https://image.tmdb.org/t/p/w500' + imageSRC}
                        alt="Pôster do filme"
                        className="p-2 cardImage"
                        fluid
                    />
                </MDBCol>
                <MDBCol md="6">
                    <MDBCardBody>
                        <MDBCardTitle>{name}</MDBCardTitle>
                        <small className="text-warning">
                            <MDBIcon far icon="star" className="me-2" />
                            {rating.toFixed(1)}
                        </small>
                        <MDBCardText>
                            {tagline && (
                                <small style={{ fontStyle: 'italic' }}>
                                    "{tagline}"
                                </small>
                            )}
                        </MDBCardText>

                        <MDBRow>
                            <MDBCol className="text-success">
                                <small>
                                    teste
                                    <MDBIcon
                                        fas
                                        icon="calendar-alt"
                                        className="me-2"
                                    />
                                    {formatDate(releaseDate)}
                                </small>
                            </MDBCol>
                        </MDBRow>
                        <MDBRow>
                            <MDBCol className="text-success">
                                <small>
                                    <span className="text-white me-2">
                                        Orçamento:
                                    </span>
                                    <MDBIcon
                                        fas
                                        icon="dollar-sign"
                                        className="me-2"
                                    />
                                    {budget > 0 && priceSplitter(budget)}
                                    {budget <= 0 && '-'}
                                </small>
                            </MDBCol>
                        </MDBRow>

                        <MDBRow>
                            <MDBCol>
                                <small>
                                    <MDBCardText className="m-0 g-0">
                                        Receita:
                                    </MDBCardText>
                                </small>
                            </MDBCol>
                        </MDBRow>
                        <MDBRow>
                            <MDBCol className="text-success">
                                <small>
                                    <span className="text-white me-2">
                                        Receita:
                                    </span>
                                    <MDBIcon
                                        fas
                                        icon="dollar-sign"
                                        className="me-2"
                                    />
                                    {revenue > 0 && priceSplitter(revenue)}
                                    {revenue <= 0 && '-'}
                                </small>
                            </MDBCol>
                        </MDBRow>
                    </MDBCardBody>
                </MDBCol>
            </MDBRow>
        </MDBCard>
    );
};

export default Movie;
